import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const feedback = [
    {
      name: '1',
      id: '001',
      image: '../images/hotline.png',
    },
    {
      name: '2',
      id: '002',
      image: '../images/neonjungle.png',
    },
    {
      name: '3',
      id: '003',
      image: '../images/3hitpay.png',
    },
    {
      name: '4',
      id: '004',
      image: '../images/dayofthedead.png',
    },
    {
      name: '5',
      id: '005',
      image: '../images/mrjekyll.png',
    },
    {
      name: '6',
      id: '006',
      image: '../images/dayofthedead.png',
    },
    {
      name: '7',
      id: '007',
      image: '../images/seaoftranquility.png',
    },
    {
      name: '8',
      id: '008',
      image: '../images/aloha.png',
    },
    {
      name: '9',
      id: '009',
      image: '../images/mrjekyll.png',
    },
    {
      name: '10',
      id: '010',
      image: '../images/dayofthedead.png',
    },
  ]

const RatingBox = styled.div`
        margin-top: 50px;
		background-color: #eee;
        padding: 70px 70px;
        text-align: center;

        .button-wrapper {
            display: inline;
        }
        .active {
            border-radius: 50px!important;
            width: 40px;
            height: 40px;
            line-height: 1;
            margin: 4px;
            color: #fff;
            background-color: #00a8a5;
            font-weight: bold;
        }

        .button-circle {
            border-radius: 50px!important;
            width: 40px;
            height: 40px;
            line-height: 1;
            margin: 4px;
            color: #fff;
            background-color: #adb5bd;
            font-weight: bold;
        }

        .button-action {
            background-color: #e40521;
            color: #ffffff;
            font-weight: bold;
            width: 150px;
            margin-top: 50px;
        }

        .error-message {
            color: #e40521;
        }
`;

export default function RatingFeedback() {

    const [activeTab, setActiveTab] = useState('');
    const [showMessage, setShowMessage] = useState(false);
    const [showThankYouMessage, setShowThankYouMessage] = useState(false);
    const [toggle, setToggle] = useState();
    const [visible, setVisible] = useState(true);

    const handleClickSubmit = () => {
        if (activeTab == '') {
            setShowMessage(true)
        } else {
            setShowMessage(false)
            setShowThankYouMessage(true);
        }
    }

	return (
        <RatingBox>
            <>
                {!showThankYouMessage && (
                    <>
                        <h3 className="font-weight-bold pb-4">
                            How did we do?
                        </h3>
                        <p className="list-details__item grey-text">
                            On a scale from 1 - 10, how easy did you find booking your hotel experience?
                        </p>

                        {feedback.map(({rate}) => {

                            <>
                                <img className="img-grid" src={rate.image} alt={rate.name} />

                                {visible && rate === "1" ? (
                                    <img src={rate.image} key={rate.id} className="photo" />
                                ) : null}
                                
                                {toggle === rate ? (
                                    <>
                                        <img src={rate.image} key={rate.id} className="photo" />
                                    </>
                                ) : null}

                                <div className="button-wrapper" key={rate.id}>
                                    <button 
                                    className={activeTab == `${rate.name}` ? 'active' : 'button-circle'} 
                                    onClick={() => {
                                        setActiveTab(`${rate.name}`), 
                                        setShowMessage(false), 
                                        setToggle(`${rate.name}`), 
                                        setVisible(false)
                                    }}>
                                        {rate.name}
                                    </button>
                                </div>
                            </>
                        })}
                        <div>
                            <button className="button-action" onClick={handleClickSubmit}>Submit</button>
                            { showMessage ? <div className="mt-3 error-message"><span>Please choose a rating</span></div> : null }
                        </div>
                    </>
                )}

                {showThankYouMessage && (
                    <>
                        <h3 className="font-weight-bold pb-4">
                            Thank you
                        </h3>
                        <p className="list-details__item grey-text small-text">
                            Your feedback helps us to improve!
                            <br/>If you have any further feedback, please feel free to <span className="red-text" style={{'text-decoration': 'underline'}}>contact us</span>
                        </p>
                    </>
                )}
            </>
        </RatingBox>
  	);
}
